import { StyleSheet, View, Text, Button, TouchableOpacity } from "react-native";
import React, { useState, useRef } from "react";
import { Image } from "expo-image";

const TimerComponent = ({ duration }) => {
  const initialTime = duration * 60; // 10 minutes in seconds
  const [time, setTime] = useState(initialTime);
  const [running, setRunning] = useState(false);
  const intervalRef = useRef(null);

  const startTimer = () => {
    if (!running) {
      intervalRef.current = setInterval(() => {
        setTime((prevTime) => {
          if (prevTime > 0) {
            return prevTime - 1;
          } else {
            clearInterval(intervalRef.current);
            setRunning(false);
            return 0;
          }
        });
      }, 1000);
      setRunning(true);
    }
  };

  const stopTimer = () => {
    clearInterval(intervalRef.current);
    setRunning(false);
  };

  const resetTimer = () => {
    clearInterval(intervalRef.current);
    setTime(initialTime);
    setRunning(false);
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return ` ${minutes}:${remainingSeconds < 10 ? "0" : ""}${remainingSeconds}`;
  };

  return (
    <View style={styles.timerContainer}>
      <View style={styles.timeText}>
        <Text style={styles.label}>Session Ends in</Text>
        <Text style={styles.timerText}>{formatTime(time)}</Text>
      </View>

      <TouchableOpacity onPress={running ? stopTimer : startTimer}>
        <Image style={styles.icon} source={require("../assets/play.svg")} />
      </TouchableOpacity>

      <TouchableOpacity onPress={resetTimer}>
        <Image
          style={styles.resetIcon}
          source={require("../assets/reset.svg")}
        />
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  timerContainer: {
    alignItems: "center",
    justifyContent: "center",
    flexDirection: "row",
    borderWidth: 1,
    borderRadius: 25,
    borderColor: "#000",
    padding: 10,
    width: "20%",
    margin: 10,
  },
  label: {
    opacity: 0.8,
  },
  timeText: {},
  timerText: {
    fontSize: 20,
    marginBottom: 10,
    marginRight: 10,
  },
  button: {
    margin: 100,
  },
  icon: {
    width: 50,
    height: 50,
  },
  resetIcon: {
    width: 25,
    height: 25,
  },
});

export default TimerComponent;
