import React, { useState, useEffect } from "react";
import { Text, View, SafeAreaView, StyleSheet } from "react-native";
import { useSocketStore } from "../store";
import socketSingleton from "../SocketManager";
import HatColor from "./HatColor";
import TimerComponent from "./TimerComponent";
import UserCard from "./UserCard";

const ParticipantsScreen = ({ navigation }) => {
  const [participants, setParticipants] = useState([]);
  const [user, setUser] = useState({}); // current user
  const [socket, setSocket] = useState(null);
  const [roomId, setRoomId] = useState(null);
  const [admin, setAdmin] = useState(null);
  const [mySession, setMySession] = useState(null);
  const [userId, setUserId] = useState(null);
  const [hatColor, setHatColor] = useState(null);
  const [timer, setTimer] = useState(null);
  const [duration, setDuration] = useState(null);
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [isTimerPaused, setIsTimerPaused] = useState(false);
  const [isTimerStopped, setIsTimerStopped] = useState(true);
  const [isTimerCompleted, setIsTimerCompleted] = useState(false);
  const [isTimerCancelled, setIsTimerCancelled] = useState(false);
  const [isTimerReset, setIsTimerReset] = useState(false);

  const [isHatColorSelected, setIsHatColorSelected] = useState(false);
  const [isHatColorSubmitted, setIsHatColorSubmitted] = useState(false);
  

  

};
