import { View, StyleSheet } from "react-native";
const HatColor = ({ hatColor }) => {
  const styles = StyleSheet.create({
    block: {
      width: "60%",
      height: "80%",
      borderRadius: 10,
      backgroundColor: "red",
    },
    color: {
      backgroundColor: {hatColor},
    },
  });

  return (
    <>
      <View style={[styles.block, styles.color]}></View>
    </>
  );
};

export default HatColor;
