import {
  View,
  Text,
  TextInput,
  SafeAreaView,
  StyleSheet,
  Button,
  TouchableOpacity,
} from "react-native";

import { useState, useEffect } from "react";
import { SocketManager } from "../SocketManager";

const JoinSession = ({ navigation }) => {
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    const socket = SocketManager.getSocket();
    setSocket(socket);
    setUserId(socket.id);
  }, []);

