import React, { useState, useEffect } from "react";
import { Text, View, SafeAreaView } from "react-native";
import { SocketManager } from "../SocketManager";

const ParticipantsScreen = ({ navigation }) => {
  const [socket, setSocket] = useState(null);
  const [userId, setUserId] = useState("");
  const [participants, setParticipants] = useState([]);

  if(socket)

  useEffect(() => {
    const socket = SocketManager.getSocket();
    setSocket(socket);
    setUserId(socket.id);

    // Listen for the roomJoined event
    socket.on("roomJoined", ({ room }) => {
      // Update participants state with the received data
      setParticipants(room.users);
    });

    // Clean up the event listener when the component unmounts
    return () => {
      socket.off("roomJoined");
    };
  }, []);

  return (
    <SafeAreaView>
      <View>
        <Text>Participants Screen</Text>
        <Text>Participants in the room:</Text>
        {participants.map((participant) => (
          <Text key={participant.id}>{participant.name}</Text>
        ))}
      </View>
    </SafeAreaView>
  );
};

export default ParticipantsScreen;
