import {
  View,
  Text,
  TextInput,
  SafeAreaView,
  StyleSheet,
  Button,
  TouchableOpacity,
} from "react-native";

import { useState, useEffect } from "react";
import socketSingleton from "../SocketManager";
import { useSocketStore, useUserStore } from "../store";

const JoinSession = ({ navigation }) => {
  const [roomId, setroomId] = useState("");

  const { userId, setUserId, isAdmin, setAdmin, setMySession } =
    useSocketStore();

  const { userName, userDesignation } = useUserStore((state) => state);

  const { socket, setSocket } = useSocketStore((state) => state);

  useEffect(() => {
    console.log("_______JoinSession.js_______");
    console.log("socket initialize: ");

    const socketConnection = async () => {
      let temp1 = await socketSingleton.initSocket();
      let temp2 = await socketSingleton.getSocket();
      setSocket(temp2);
    };
    socketConnection();
  }, []);
  const joinRoom = () => {
    
    console.log("Joining room...");
    const newUserId = socket.id;
    setUserId(newUserId);

    const data = {
      roomId,
      userId,
      userName,
      userDesignation,
    };

    socket.emit("joinRoom", data);

    socket.on("userInfo", (data) => {
      console.log("userInfo event received: ", data);
      // setUserId(data.userId);
      // setAdmin(data.isAdmin);
    });
    setAdmin(false);
    setMySession((prev) => prev.append(data));

    navigation.navigate("Participants", { roomId, userId, isAdmin: false });
  };

  return (
    <SafeAreaView>
      <View>
        <Text>Join Session</Text>
        <TextInput
          style={styles.codeTextBox}
          onChangeText={(text) => {
            setroomId(text);
          }}
        />
      </View>

      <Button title="Join" onPress={joinRoom} />
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  codeTextBox: {
    borderWidth: 1,
    borderColor: "black",
    borderRadius: 5,
    padding: 5,
    width: 200,
  },
});

export default JoinSession;
