import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  TextInput,
} from "react-native";
import { useState } from "react";
import { Color, FontFamily, FontSize, Padding, Border } from "../GlobalStyles";

const ProfilePage = () => {
  const [name, setName] = useState("");
  const [organisation, setOrganisation] = useState("");
  const [role, setRole] = useState("");
  const [agreeTerms, setAgreeTerms] = useState(false);

  return (
    <View style={styles.container}>
      <View style={styles.inputContainer}>
        <Text style={styles.label}>Title</Text>
        <TextInput
          style={[styles.input, styles.codeTextBox]}
          onChangeText={(text) => setTitle(text)}
          value={title}
        />
      </View>
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    flexDirection: "column",
    justifyContent: "space-between",
    backgroundColor: "#fff",
    paddingVertical: 30,
    paddingHorizontal: 20,
    height: "100%",
    textAlign: "center",
    alignContent: "center",
    fontFamily: FontFamily.manropeSemiBold,
  },
  header: {
    fontFamily: FontFamily.manropeSemiBold,
    fontSize: FontSize.size_base,
    color: Color.black,
    fontWeight: "600",
    lineHeight: 20,
  },
  inputContainer: {
    textAlign: "left",
  },
  label: {
    opacity: 0.5,
    marginBottom: 3,
  },
  codeTextBox: {
    borderWidth: 2,
    borderColor: "black",
    borderRadius: 5,
    borderWidth: 1,
    width: 300,
    textAlignVertical: "top",
    paddingVertical: 2,
    paddingHorizontal: 4,
  },
  description: {
    height: 100,
  },
  durationContainer: {
    flexDirection: "row",
    justifyContent: "space-around",
    alignItems: "center",
    width: "80%",
  },
  durationButton: {
    backgroundColor: "#fff",
    padding: 10,
    borderRadius: 5,
    borderWidth: 1,
    borderColor: "#000",
  },
  durationText: {
    color: "#000",
  },
  selectedButton: {
    borderColor: "green",
  },
  createButton: {
    backgroundColor: "black",
    borderRadius: 25,
  },
  buttonText: {
    color: "white",
    fontSize: 16,
    padding: 10,
    textAlign: "center",
  },
});

export default ProfilePage;
