import React, { useEffect } from "react";
import { View, StyleSheet, TouchableOpacity, SafeAreaView } from "react-native";
import { useNavigation } from "@react-navigation/native";
import { horizontalScale, verticalScale } from "../../metric";
import { useUserStore } from "../../store";
import { Image } from "expo-image";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { Avatar, Button } from "react-native-paper";
import { Surface } from "react-native-paper";

const Navbar = ({ navigation }) => {
  const [showProfileMenu, setshowProfileMenu] = React.useState(false);

  const {
    userName,
    userOrganisation,
    userRole,
    userProfilePic,
    setUserName,
    setUserOrganisation,
    setUserRole,
    setUserProfilePic,
  } = useUserStore();

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        // Fetch profile data from local storage
        const storedData = await AsyncStorage.getItem("profileData");
        if (storedData) {
          const parsedData = JSON.parse(storedData);
          setUserName(parsedData.userName);
          setUserOrganisation(parsedData.userOrganisation);
          setUserRole(parsedData.userRole);
          setUserProfilePic(parsedData.userProfilePic);
          navigation.navigate("SessionScreen");
        }
      } catch (error) {
        console.error("Error fetching profile data:", error);
      }
    };

    fetchProfileData();
  }, []);

  const handleBackPress = () => {
    navigation.goBack();
  };

  const tinkLogoSource = require("../../assets/tink-logo-1.png");
  const defaultProfilePicSource = require("../../assets/ellipse-15.svg");
  const DefaultProfilePicSource = () => {
    return (
      <Avatar.Icon
        size={50}
        icon="account"
        style={{
          borderColor: "black",
          borderWidth: 1,
        }}
      />
    );
  };

  const toggleProfileMenu = () => {
    setshowProfileMenu(!showProfileMenu);
  };

  const ProfileMenu = () => {
    return (
      <Surface
        style={{
          backgroundColor: "white",
          position: "absolute",
          top: 60,
          right: 50,
          borderRadius: 10,
          zIndex: 1,
          padding: 10,
        }}
        elevation={4}
      >
        <Button
          icon="logout"
          onPress={async () => {
            await AsyncStorage.multiRemove(["profileData"]);
            navigation.navigate("Home");
          }}
          textColor="black"
        >
          Logout
        </Button>
      </Surface>
    );
  };

  return (
    <SafeAreaView>
      <View style={styles.navbar}>
        <TouchableOpacity onPress={handleBackPress}>
          <Image
            style={styles.icon}
            source={require("../../assets/menu.svg")}
          />
        </TouchableOpacity>

        <Image style={styles.tinkLogo} source={tinkLogoSource} />

        <TouchableOpacity onPress={toggleProfileMenu}>
          <Avatar.Image
            size={50}
            source={
              userProfilePic
                ? { uri: userProfilePic }
                : require("../../assets/ellipse-15.svg")
            }
          />
        </TouchableOpacity>
        {showProfileMenu && <ProfileMenu />}
      </View>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  navbar: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    backgroundColor: "white",
    marginTop: verticalScale(20),
    paddingHorizontal: horizontalScale(20),
    paddingVertical: verticalScale(20),
  },
  icon: {
    width: 50,
    height: 50,
    borderRadius: 25,
  },
  tinkLogo: {
    width: 106,
    height: 32,
  },
});

export default Navbar;
