import io from "socket.io-client";

let socket;

const initSocket = () => {
    socket = io("http://192.168.100.17:8000")
    return socket;
}

const getSocket = () => {
    if (!socket) {
        throw new Error("Call init first");
    }
    return socket;
}

export { initSocket, getSocket };
