import React, { useState, useEffect } from "react";
import { Text, View, SafeAreaView, StyleSheet } from "react-native";
import { useSocketStore } from "../store";
import socketSingleton from "../SocketManager";
import HatColor from "./HatColor";
import TimerComponent from "./TimerComponent";
import UserCard from "./UserCard";
const { useUserStore } = require("../store");

const ParticipantsScreen = ({ navigation }) => {
  const [socket, setSocket] = useState(null);
  const [isAdmin, setIsAdmin] = useState(true);

  const { userName, userOrganisation, userDesignation, userProfilePic } =
    useUserStore();

  useEffect(() => {
    setSocket(async () => await socketSingleton.getSocket());
  }, []);

  let sampleParticipants = [
    {
      id: "6AxDMLtjF4UcvLLOAAAJ",
      name: "Manav Malhotra",
      Role: "Developer",
      hatRole: "blue",
      isAdmin: true,
    },
    {
      id: "1",
      name: "Dummy User 2",
      role: "Dummy Role 2",
      hatRole: "red",
      isAdmin: false,
    },
    {
      id: "1",
      name: "Dummy User 2",
      role: "Dummy Role 2",
      hatRole: "white",
      isAdmin: false,
    },
  ];

  const [participants, setParticipants] = useState(sampleParticipants);

  return (
    <SafeAreaView>
      {isAdmin ? (
        <View>
          <TimerComponent duration="10" />
          <Text>Participants in the room:</Text>
          {/* <Text>Admin ID: {userId}</Text>
          <Text>Room ID: {roomId}</Text> */}
          {
            // rendeer the list of participants
            participants.map((user) => {
              return (
                <View key={user.id}>
                  <UserCard key={user.id} user={userInfo} />
                  <Text style={styles.participant}>
                    {user.name} : {user.id}: {user.hatRole}
                  </Text>
                </View>
              );
            })
          }
        </View>
      ) : (
        <View style={styles.participant}>
          <Text>User ID: {userId}</Text>
          <Text>Room ID: {roomId}</Text>

          <HatColor hatColor={hatColor} />
        </View>
      )}
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  participant: {
    fontSize: 20,
    fontWeight: "bold",
  },
});

export default ParticipantsScreen;
