import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  Button,
  TouchableOpacity,
} from "react-native";
import { Image } from "expo-image";
import { horizontalScale, moderateScale, verticalScale } from "../metric";

const SessionScreen = ({ navigation }) => {
  return (
    <SafeAreaView>
      <View style={styles.groupParent}>
        <View style={styles.groupWrapper}>
          <Text style={styles.mySession}>My Sessions</Text>
          <Text style={[styles.history]}>History</Text>
        </View>

        <Image
          style={styles.noSession}
          contentFit="cover"
          source={require("../assets/no-session.svg")}
        />

        <View style={styles.sessionBtn}>
          <TouchableOpacity
            style={styles.createButton}
            onPress={() => navigation.navigate("NewSession")}
          >
            <Text style={styles.buttonText}>+ Create new Session</Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={styles.createButton}
            onPress={() => navigation.navigate("JoinSession")}
          >
            <Text style={styles.buttonText}>Join Session</Text>
          </TouchableOpacity>
        </View>
      </View>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  groupParent: {
    flexDirection: "column",
    justifyContent: "space-between",
    backgroundColor: "#fff",
    height: "100%",
    textAlign: "center",
    alignContent: "center",
    paddingHorizontal: horizontalScale(20),
    paddingVertical: verticalScale(20),
    flex: 1,
  },
  groupWrapper: {
    flexDirection: "row",
    alignItems: "center",
    paddingHorizontal: 20,
    gap: 20,
  },
  mySession: {
    fontWeight: "bold",
  },
  noSession: {
    width: 220,
    height: 220,
    alignSelf: "center",
  },
  sessionBtn: {
    alignContent: "flex-end",
  },
  joinSessionBtn: {
    marginTop: 20,
  },
  createButton: {
    borderRadius: 25,
    borderColor: "#000",
    borderWidth: moderateScale(1),
    paddingHorizontal: horizontalScale(30),
    paddingVertical: verticalScale(40),
    marginVertical: verticalScale(10),
    marginHorizontal: horizontalScale(20),
  },
  buttonText: {
    textAlign: "center",
    
  },
});

export default SessionScreen;
