import React, { useState, useEffect } from "react";
import { Text, View, SafeAreaView, StyleSheet } from "react-native";
import { useSocketStore } from "../store";
import socketSingleton from "../SocketManager";
import HatColor from "./HatColor";
import TimerComponent from "./TimerComponent";
import UserCard from "./UserCard";

const ParticipantsScreen = ({ navigation, route }) => {
  const [participants, setParticipants] = useState([]);
  const [socket, setSocket] = useState(null);

  const userId = useSocketStore((state) => state.userId);
  const isAdmin = useSocketStore((state) => state.isAdmin);
  const hatColor = useSocketStore((state) => state.hatColor);
  const roomId = useSocketStore((state) => state.roomId);
  const setHatColor = useSocketStore((state) => state.setHatColor);

  useEffect(() => {
    const socket = socketSingleton.getSocket();
    setSocket(socket);

    // Listen for the roomData event
    socket.on("roomData", (roomData) => {
      const roomIdKey = Object.keys(roomData)[0]; // Assuming roomIdKey is "y4b4cq"
      const updatedUsers = roomData[roomIdKey].users.map((user) => ({
        ...user,
        name: user.name || "Anonymous",
      }));

      // Update participants state with the received data
      setParticipants(updatedUsers);

      // Find the user's hat color and set it using setHatColor
      const currentUser = roomData[roomIdKey].users.find(
        (user) => user.id === userId
      );
      if (currentUser) {
        setHatColor(currentUser.hatRole);
      }
    });

    // Clean up the socket listener when the component unmounts
    return () => {
      socket.off("roomData");
    };
  }, [userId, setHatColor]);

  const userInfo = {
    name: "Anonymous",
    designation: "Developer",
    profilePic: require("../assets/userProfilePic.png"),
  };

  return (
    <SafeAreaView>
      {isAdmin ? (
        <View>
          <TimerComponent duration="10" />
          <Text>Participants in the room:</Text>
          <Text>Admin ID: {userId}</Text>
          <Text>Room ID: {roomId}</Text>
          {participants.map((participant) => (
            <Text style={styles.participant} key={participant.id}>
              {participant.id} : {participant.hatRole}
            </Text>
          ))}
          <UserCard user={userInfo} />
        </View>
      ) : (
        <View style={styles.participant}>
          <Text>User ID: {userId}</Text>
          <Text>Room ID: {roomId}</Text>

          <HatColor hatColor={hatColor} />
        </View>
      )}
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  participant: {
    fontSize: 20,
    fontWeight: "bold",
  },
});

export default ParticipantsScreen;
