import { create } from "zustand";
import io from "socket.io-client";

const useSocketStore = create((set) => ({
  socket: "",
  userId: null,
  isAdmin: null,
  hatColor: null,
  roomId: null,
  mySession: [],
  connect: () => {
    const socket = io("http://192.168.100.17:8000", (data) => {
      console.log("Socket created:", socket);
      console.log("data: ", data);
    });
    console.log("Connecting socket:", socket);
    set({ socket: socket });
    set((state) => ({ userId: socket.id, ...state }));
    return socket; // Return the socket value
  },

  disconnect: () => {
    const socket = useSocketStore.getState().socket;
    if (socket) {
      socket.disconnect();
      set({ socket: null });
    }
  },
  setSocket: (socket) => set({ socket }),
  setuserId: (userId) => set({ userId }),
  setAdmin: (isAdmin) => set({ isAdmin }),
  setHatColor: (hatColor) => set({ hatColor }),
  setroomId: (roomId) => set({ roomId }),
  setMySession: (newSession) => set({ mySession: newSession }),
}));

const useUserStore = create((set) => ({
  // user name
  // user organisation
  // user designation
  // user profile pic

  userName: null,
  userOrganisation: null,
  userDesignation: null,
  userProfilePic: null,

  setUserName: (userName) => set({ userName }),
  setUserOrganisation: (userOrganisation) => set({ userOrganisation }),
  setUserDesignation: (userDesignation) => set({ userDesignation }),
  setUserProfilePic: (userProfilePic) => set({ userProfilePic }),
}));

export { useSocketStore, useUserStore };
