import React, { useState, useEffect } from "react";
import {
  Text,
  View,
  TextInput,
  StyleSheet,
  TouchableOpacity,
} from "react-native";
import { useSocketStore, useUserStore } from "../store";
import socketSingleton from "../SocketManager";
import { FontFamily, FontSize, Color } from "../GlobalStyles";
import { Input } from "@rneui/themed";

const NewSession = ({ navigation }) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [selectedDuration, setSelectedDuration] = useState(null);
  const [capacity, setCapacity] = useState("");
  const [duration, setDuration] = useState("");

  const [socket, setSocket] = useState(null);

  // const roomId = useSocketStore((state) => state.roomId);
  // const setAdmin = useSocketStore((state) => state.setAdmin);
  // const setroomId = useSocketStore((state) => state.setroomId);
  // const setMySession = useSocketStore((state) => state.setMySession);
  // const setUserId = useSocketStore((state) => state.setUserId);
  // const userId = useSocketStore((state) => state.userId);

  const { userName, userOrganisation, userRole, userProfilePic } = useUserStore();
  const { userId, setUserId, setroomId } = useSocketStore();

  const handleDurationSelection = (duration) => {
    setSelectedDuration(duration);
  };

  const createRoom = () => {
    const socketConnection = async () => {
      await socketSingleton.initSocket();
      const temp = await socketSingleton.getSocket();
      setSocket(temp);
      setUserId(socket.id);
      console.log("socket id: ", socket.id)

    };
    socketConnection();
    console.log("Creating room...");

    // const {
    //   title,
    //   description,
    //   duration,
    //   capacity,
    //   userId,
    //   userName,
    //   userRole,
    // } = data;


    const data = {
      title,
      description,
      duration: selectedDuration,
      capacity,
      userId,
      userName,
      userRole,
    };

    socket.emit("createRoom", data);

    socket.on("roomCreated", (data) => {
      console.log("roomCreated: ", data.roomId);
      setroomId(data.roomId);
    });

    navigation.navigate("Participants");
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>New Session</Text>

      
    </View>
  );
};

const buttonStyles = {
  backgroundColor: "black",
  borderRadius: 25,
};

const styles = StyleSheet.create({
  container: {
    flexDirection: "column",
    justifyContent: "space-between",
    backgroundColor: "#fff",
    paddingVertical: 30,
    paddingHorizontal: 20,
    height: "100%",
    textAlign: "center",
    alignContent: "center",
    fontFamily: FontFamily.manropeSemiBold,
  },
  header: {
    fontFamily: FontFamily.manropeSemiBold,
    fontSize: FontSize.size_base,
    color: Color.black,
    fontWeight: "600",
    lineHeight: 20,
  },
  inputContainer: {
    textAlign: "left",
  },
  label: {
    opacity: 0.5,
    marginBottom: 3,
  },
  codeTextBox: {
    borderWidth: 2,
    borderColor: "black",
    borderRadius: 5,
    borderWidth: 1,
    width: 300,
    textAlignVertical: "top",
    paddingVertical: 2,
    paddingHorizontal: 4,
  },
  description: {
    height: 100,
  },
  durationContainer: {
    flexDirection: "row",
    justifyContent: "space-around",
    alignItems: "center",
    width: "80%",
  },
  durationButton: {
    backgroundColor: "#fff",
    padding: 10,
    borderRadius: 5,
    borderWidth: 1,
    borderColor: "#000",
  },
  durationText: {
    color: "#000",
  },
  selectedButton: {
    borderColor: "green",
  },
  createButton: {
    ...buttonStyles,
  },
  buttonText: {
    color: "white",
    fontSize: 16,
    padding: 10,
    textAlign: "center",
  },
});

export default NewSession;
