import React, { useState } from "react";
import { View, StyleSheet } from "react-native";

const HatColor = ({ hatColor }) => {
  const[newhatColor, setHatColor] = useState("");
  setHatColor(hatColor);
  const styles = StyleSheet.create({
    block: {
      width: "30%",
      height: "30%",
      borderRadius: 10,
      position: "fixed",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      marginTop: 50,
    },
    color: {
      backgroundColor: newhatColor,
    },
  });

  return <View style={[styles.block, styles.color]}></View>;
};

export default HatColor;
