import React, { useState, useEffect } from "react";
import { Text, View, SafeAreaView, StyleSheet } from "react-native";
import { useSocketStore } from "../store";
import socketSingleton from "../SocketManager";
import HatColor from "./HatColor";
import TimerComponent from "./TimerComponent";
import UserCard from "./UserCard";
const { useUserStore } = require("../store");

const ParticipantsScreen = ({ navigation }) => {
  const { socket, setSocket } = useSocketStore();

  const [participants, setParticipants] = useState([]);

  const { userName, userOrganisation, userDesignation, userProfilePic,  } =
    useUserStore();

  const { userId, setUserId, roomId, setRoomId, hatColor, setHatColor, isAdmin } =
    useSocketStore();

  useEffect(() => {
    const socketConnection = async () => {
      let temp2 = await socketSingleton.getSocket();
      console.log("temp2: ", temp2);
      await setSocket(temp2);
    };
    socketConnection();
  }, []);

  // listen for roomjoined event from server and update the participants list

  useEffect(() => {
    socket.on("roomJoined", (data) => {
      console.log("roomJoined event received: ", data);
      setParticipants(data);
    });

    return () => {
      socket.off("roomJoined");
    };
  }, []);

  let sampleParticipants = [
    {
      id: "6AxDMLtjF4UcvLLOAAAJ",
      name: "Manav Malhotra",
      role: "Developer",
      hatRole: "blue",
      isAdmin: true,
    },
    {
      id: "1",
      name: "Dummy User 2",
      role: "Dummy Role 2",
      hatRole: "red",
      isAdmin: false,
    },
    {
      id: "1",
      name: "Dummy User 2",
      role: "Dummy Role 2",
      hatRole: "white",
      isAdmin: false,
    },
  ];

  return (
    <SafeAreaView>
      {isAdmin ? (
        <View>
          <TimerComponent duration="10" />
          <Text>Participants in the room:</Text>
          <Text>Admin ID: {userId}</Text>
          <Text>Room ID: {roomId}</Text>
          {
            // rendeer the list of participants
            participants.map((user) => {
              return (
                <View key={user.id}>
                  <UserCard key={user.id} user={user} />
                </View>
              );
            })
          }
        </View>
      ) : (
        <View style={styles.participant}>
          <Text>User ID: {userId}</Text>
          <Text>Room ID: {roomId}</Text>

          <HatColor hatColor={hatColor} />
        </View>
      )}
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  participant: {
    fontSize: 20,
    fontWeight: "bold",
  },
});

export default ParticipantsScreen;
