import React, { useState } from "react";
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  TextInput,
} from "react-native";
import { Color, FontFamily, FontSize, Padding, Border } from "../GlobalStyles";
import { CheckBox } from "react-native-elements";
import { Input } from "@rneui/themed";

import { horizontalScale, verticalScale, moderateScale } from "../metric";

import { Image } from "expo-image";

const ProfilePage = (navigation) => {
  const [name, setName] = useState("");
  const [organisation, setOrganisation] = useState("");
  const [role, setRole] = useState("");
  const [checked, setChecked] = React.useState(false);
  const input = React.createRef();

  const createProfile = () => {
    if (!name || !organisation || !role) {
      alert("Please fill all fields");
      return;
    } else if (!checked) {
      alert("Please agree to the terms and conditions");
      return;
    } else {
      console.log("Creating profile...");
      const data = { name, organisation, role };
      console.log("data: ", data);
    }
  };

  return (
    <View style={styles.container}>
      <Image
        style={{ width: 100, height: 100, alignSelf: "center" }}
        source={require("../assets/imageUpload.svg")}
      />

      {/* <View style={styles.inputContainer}>
        <Text style={styles.label}>Full Name</Text>
        <TextInput
          style={[styles.input, styles.codeTextBox]}
          onChangeText={(text) => setName(text)}
          value={name}
        />
      </View>
      <View style={styles.inputContainer}>
        <Text style={styles.label}>Organisation</Text>
        <TextInput
          style={[styles.input, styles.codeTextBox]}
          onChangeText={(text) => setOrganisation(text)}
          value={organisation}
        />
      </View>
      <View style={styles.inputContainer}>
        <Text style={styles.label}>Role</Text>
        <TextInput
          style={[styles.input, styles.codeTextBox]}
          onChangeText={(text) => setRole(text)}
          value={role}
        />
      </View> */}

      <Input
        label="Full Name"
        labelStyle={styles.label}
        onChangeText={(text) => setName(text)}
        value={name}
        inputStyle={[styles.inputContainer]}
        inputContainerStyle={styles.codeTextBox}
      />
      <Input
        label="Organisation"
        labelStyle={styles.label}
        onChangeText={(text) => setOrganisation(text)}
        value={organisation}
        inputStyle={[styles.input]}
        inputContainerStyle={styles.codeTextBox}
      />
      <Input
        label="Role"
        labelStyle={styles.label}
        onChangeText={(text) => setRole(text)}
        value={role}
        inputStyle={[styles.input]}
        inputContainerStyle={styles.codeTextBox}
      />

      <CheckBox
        checked={checked}
        containerStyle={styles.checkBoxContainer}
        checkedColor={{ color: "#000000", opacity: 0.5 }}
        uncheckedColor={{ color: "#000000", opacity: 0.5 }}
        onIconPress={() => setChecked(!checked)}
        size={30}
        textStyle={{ fontFamily: FontFamily.manropeRegular, fontWeight: "400" }}
        title="I agree to receive updates and newsletter from this application"
      />

      <TouchableOpacity style={styles.createProfile} onPress={createProfile}>
        <Text style={styles.buttonText}>Continue</Text>
      </TouchableOpacity>
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    flexDirection: "column",
    backgroundColor: "#fff",
    paddingVertical: 30,
    paddingHorizontal: 20,
    height: "100%",
    textAlign: "center",
    alignContent: "center",
    fontFamily: FontFamily.manropeSemiBold,
  },
  label: {
    opacity: 0.5,
    fontFamily: FontFamily.manropeSemiBold,
    fontStyle: "normal",
    fontWeight: "400",
    textAlign: "left",
  },

  inputContainer: {
    fontFamily: FontFamily.manropeSemiBold,
    fontSize: FontSize.size_base,
    color: Color.black,
    fontWeight: "600",
  },
  codeTextBox: {
    borderWidth: 1,
    borderColor: "black",
    borderRadius: 8,
    paddingHorizontal: 10,
  },
  checkBoxContainer: {
    backgroundColor: "transparent",
    borderWidth: 0,
    padding: 0,
    margin: 0,
  },
  buttonText: {
    color: "black",
    fontSize: 16,
    padding: 10,
    textAlign: "center",
  },
  createProfile: {
    backgroundColor: "transparent",
    borderRadius: 100,
    borderWidth: 1,
    marginVertical: 10,
    paddingVertical: 8,
    paddingHorizontal: 5,
    fontFamily: FontFamily.manropeSemiBold,
    fontSize: FontSize.size_base,
    fontWeight: "600",
  },
});

export default ProfilePage;

