import {
  Text,
  View,
  TextInput,
  StyleSheet,
  SafeAreaView,
  Button,
  TouchableOpacity,
} from "react-native";
import { useEffect, useState } from "react";

import { SocketManager } from "../SocketManager";
import useSocketStore from "../store";

const NewSession = ({ navigation }) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [duration, setDuration] = useState("");
  const [capacity, setCapacity] = useState("");
  // const [socket, setSocket] = useState();
  // const [userId, setUserId] = useState("");

  // const { userId, isAdmin, hatColor, setuserId, setAdmin, setHatColor } =  useSocketStore();
  const socket = useSocketStore((state) => state.socket);
  const userId = useSocketStore((state) => state.userId);

  useEffect(() => {
    useSocketStore.getState().connect();
    return () => {
      useSocketStore.getState().disconnect();
    };
  }, []);

  // useEffect(() => {
  //   const socket = SocketManager.getSocket();
  //   setSocket(socket);
  //   setuserId(socket.id);
  // }, []);

  const createRoom = () => {
    console.log("Creating room...");
    console.log("socket id: ", userId);
    // set userId
    userId && useSocketStore.getState().setuserId(userId);

    const data = { userId, title, description, duration, capacity };

    socket.emit("createRoom", data);
    navigation.navigate("Participants", { userId, isAdmin: true });
  };

  return (
    <SafeAreaView>
      <View>
        <Text>Title</Text>
        <TextInput
          style={styles.codeTextBox}
          onChangeText={(text) => {
            setTitle(text);
          }}
        />
        <Text>Description</Text>
        <TextInput
          style={styles.codeTextBox}
          onChangeText={(text) => {
            setDescription(text);
          }}
        />
        <Text>Duration</Text>
        <TextInput
          style={styles.codeTextBox}
          onChangeText={(text) => {
            setDuration(text);
          }}
        />
        <Text>Capacity</Text>
        <TextInput
          style={styles.codeTextBox}
          onChangeText={(text) => {
            setCapacity(text);
          }}
        />
        <Button title="Create Room" onPress={createRoom} />
      </View>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  codeTextBox: {
    borderWidth: 1,
    borderColor: "black",
    borderRadius: 5,
    padding: 5,
    width: 200,
  },
});

export default NewSession;
