import React, { useState } from "react";
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  TextInput,
} from "react-native";
import { Color, FontFamily, FontSize, Padding, Border } from "../GlobalStyles";
import { CheckBox } from "react-native-elements";

import { Image } from "expo-image";

const ProfilePage = () => {
  const [name, setName] = useState("");
  const [organisation, setOrganisation] = useState("");
  const [role, setRole] = useState("");
  const [checked, setChecked] = React.useState(false);

  const createProfile = ({navigation}) => {
    console.log("Creating profile...");
    const data = { name, organisation, role };
    console.log("data: ", data);

    
  }

  return (
    <View style={styles.container}>
      <Image
        style={{ width: 100, height: 100, alignSelf: "center" }}
        source={require("../assets/imageUpload.svg")}
      />

      <View style={styles.inputContainer}>
        <Text style={styles.label}>Full Name</Text>
        <TextInput
          style={[styles.input, styles.codeTextBox]}
          onChangeText={(text) => setName(text)}
          value={name}
        />
      </View>
      <View style={styles.inputContainer}>
        <Text style={styles.label}>Organisation</Text>
        <TextInput
          style={[styles.input, styles.codeTextBox]}
          onChangeText={(text) => setOrganisation(text)}
          value={organisation}
        />
      </View>
      <View style={styles.inputContainer}>
        <Text style={styles.label}>Role</Text>
        <TextInput
          style={[styles.input, styles.codeTextBox]}
          onChangeText={(text) => setRole(text)}
          value={role}
        />
      </View>

      <CheckBox
        checked={checked}
        containerStyle={styles.checkBoxContainer}
        checkedColor={{ color: "#000000" }}
        uncheckedColor={{ color: "#000000" }}
        onIconPress={() => setChecked(!checked)}
        size={30}
        textStyle={{ fontFamily: FontFamily.manropeRegular }}
        title="I agree to receive updates and newsletter from this application"
      />

      <TouchableOpacity style={styles.createProfile} onPress={createProfile}>
        <Text style={styles.buttonText}>Continue</Text>
      </TouchableOpacity>
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    flexDirection: "column",
    backgroundColor: "#fff",
    paddingVertical: 30,
    paddingHorizontal: 20,
    height: "100%",
    textAlign: "center",
    alignContent: "center",
    fontFamily: FontFamily.manropeSemiBold,
  },
  header: {
    fontFamily: FontFamily.manropeSemiBold,
    fontSize: FontSize.size_base,
    color: Color.black,
    fontWeight: "600",
  },
  inputContainer: {
    textAlign: "center",
    marginHorizontal: 5,
  },
  label: {
    opacity: 0.5,
  },
  codeTextBox: {
    borderWidth: 1,
    borderColor: "black",
    borderRadius: 8,
    marginVertical: 10,
    paddingHorizontal: 5,
    paddingVertical: 8,
    fontFamily: FontFamily.manropeSemiBold,
    fontSize: FontSize.size_base,
    fontWeight: "600",
  },
  checkBoxContainer: {
    backgroundColor: "transparent",
    borderWidth: 0,
    padding: 0,
    margin: 0,
    marginVertical: 20,
  },
  buttonText: {
    color: "black",
    fontSize: 16,
    padding: 10,
    textAlign: "center",
  },
  createProfile: {
    backgroundColor: "transparent",
    borderRadius: 100,
    borderWidth: 2,
    marginVertical: 10,
    paddingVertical: 8,
    paddingHorizontal: 5,
    fontFamily: FontFamily.manropeSemiBold,
    fontSize: FontSize.size_base,
    fontWeight: "600",
  }
});

export default ProfilePage;

