/* fonts */
export const FontFamily = {
  manropeSemiBold: "Manrope-SemiBold",
  manropeRegular: "Manrope-Regular",
  rubikRegular: "Rubik-Regular",
};
/* font sizes */
export const FontSize = {
  size_base: 16,
  size_sm: 14,
};
/* Colors */
export const Color = {
  white: "#fff",
  black: "#000",
};
/* Paddings */
export const Padding = {
  p_3xs: 10,
  p_base: 16,
};
/* border radiuses */
export const Border = {
  br_5xs: 8,
};
