import {
  View,
  Text,
  TextInput,
  SafeAreaView,
  StyleSheet,
  Button,
  TouchableOpacity,
} from "react-native";

import { useState, useEffect } from "react";
import socketSingleton from "../SocketManager";
import { useSocketStore, useUserStore } from "../store";

const JoinSession = ({ navigation }) => {
  const [roomId, setroomId] = useState("");

  const { userId, setUserId, isAdmin } = useSocketStore();

  const { userName, userRole } = useUserStore();

  // Initialize the socket state
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    console.log("_______JoinSession.js_______");
    console.log("socket initialize: ");
    socketConnection();
  }, []);

  const socketConnection = async () => {
    const initializedSocket = await socketSingleton.initSocket();
    setSocket(initializedSocket);
    initializedSocket.on("connect", () => {
      console.log("socket connected: ", initializedSocket.id);
      let id = initializedSocket.id;
      setUserId(id);
      console.log("socket id: ", id);
    });
  };

  const joinRoom = () => {
    if (!socket) {
      console.log("Socket is not defined");
      return;
    }

    console.log("Joining room...");
    console.log("Socket", socket);
    console.log("User Id", userId, socket.id);

    const data = {
      roomId,
      userId,
      userName,
      userRole,
    };

    socket.emit("joinRoom", data);

    socket.on("userInfo", (data) => {
      console.log("userInfo event received: ", data);
    });

    setMySession((prev) => prev.append(data));

    navigation.navigate("Participants", { roomId, userId, isAdmin: false });
  };

  return (
    <SafeAreaView>
      <View>
        <Text>Join Session</Text>
        <TextInput
          style={styles.codeTextBox}
          onChangeText={(text) => {
            setroomId(text);
          }}
        />
      </View>

      <Button title="Join" onPress={joinRoom} />
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  codeTextBox: {
    borderWidth: 1,
    borderColor: "black",
    borderRadius: 5,
    padding: 5,
    width: 200,
  },
});

export default JoinSession;
